package s3

import (
	"reflect"
	"text/template"
)

const index = `
<!doctype html>
<html>
    <head>
        <meta charset="utf-8">
        <title>releases.manifold.co directory listing</title>
        <style>
            body {
                position: relative;
                margin: 0;
                padding: 80px 0 0;
                font-family: sans-serif;
                font-size: 18px;
                background: #f7f7f7;
            }

            body::before {
                content: '';
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 2px;
                background-image: linear-gradient(-21deg, #FF0264 0%, #FE744A 49%, #FDBC39 82%, #FDDF31 100%);
            }

            .wrapper {
                padding: 20px 30px;
                margin: 0 auto 20px;
                max-width: 840px;
                background: #FFF;
            }

            table {
                display: table;
                width: 100%;
                line-height: 1.5;
            }

            td {
                border-bottom: 1px dotted #bababa;
                padding: 5px;
            }

            td.size {
                text-align: end;
            }

            td.modified {
                text-align: end;
            }

            .parent.directory a {
                font-size: 15px;
                color: #666;
            }

            .parent.directory a:hover {
                color: #222;
            }

            .parent.directory a::before {
                content: '\2190';
                margin-right: 4px;
            }

            .footer {
                margin: 40px 0 0 0;
                padding: 20px 0;
                font-size: 13px;
                color: #858181;
                margin-top: 80px;
            }

            .copyright {
                float: right;
            }

            h1 {
                text-align: center;
                font-size: 14px;
                text-transform: uppercase;
                margin-bottom: 40px;
                font-weight: 600;
                letter-spacing: 1px;
                color: #888;
                text-shadow: 0 2px 1px #FFF;
            }

            a {
                color: #0072C4;
                text-decoration: none;
            }

            a:visited {
                color: rgba(0, 114, 196, 0.75);
            }

            a:hover {
                color: #0D86DD;
            }

            .directory .name a:before,
            .generic .name a:before {
                width: 17px;
                height: 17px;
                display: inline-block;
                margin: 0 10px 0 0;
                position: relative;
                top: 2px;
                opacity: .4;
            }

            .directory .name a:before {
                content: url(data:image/svg+xml;base64,);
            }

            .generic .name a:before {
                content: url(data:image/svg+xml;base64,);
            }

        </style>
    </head>
    <body>
        <div class="wrapper">
            <div class="listing">
                <h4>{{ .Dir }}</h4>
                <table>
                    {{ if ne .Dir "/" }}
                    <tr class="parent directory">
                        <td><a href="../">parent directory</a></td>
                        <td></td>
                        <td></td>
                    </tr>
                    {{ end }}
                    {{ range .Files }}
                    <tr class="{{ .Type }}">
                        <td class="name"><a href="{{ .Name }}">{{ .Name }}{{ if eq .Type "directory" }}/{{ end }}</a></td>
                        <td class="size">{{ filterZero .Size }}</td>
                        <td class="modified">{{ filterZero .Modified }}</td>
                    </tr>
                    {{ end }}
                </table>
            </div>
            <div class="footer">
                <span class="copyright">Â© 2016 - 2017 Manifold</span>
                <span class="timestamp">Index generated at {{ .Timestamp }}</span>
            </div>
        </div>
    </body>
</html>
`

var tmpl = template.Must(template.New("index.html").Funcs(template.FuncMap{
	"filterZero": func(v interface{}) interface{} {
		if v == reflect.Zero(reflect.TypeOf(v)).Interface() {
			return ""
		}

		return v
	},
}).Parse(index))
